
public class EjercicioCuatro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double x, y;
		
		x = 3 + 4/5;
		y = (3 + 4) / 5;
		
		System.out.println("x = " + x);
		System.out.println("y = " + y);
		
		System.out.println("Regla PEMDAS: ");
		System.out.println("  1) Paréntesis");
		System.out.println("  2) Exponentes/Raices");
		System.out.println("  3) Multiplicador");
		System.out.println("  4) Divisor");
		System.out.println("  5) Adición");
		System.out.println("  6) Substracción");

	}

}

